import IconBackground from "@/assets/svg/svg_background.svg";

const { $anime } = useNuxtApp();

onMounted(() => {
  $anime({
    targets: ".path",
    strokeDashoffset: function (el) {
      var pathLength = el.getTotalLength();
      el.setAttribute("stroke-dasharray", pathLength);
      return [-pathLength, 0];
    },
    stroke: {
      value: function (el, i) {
        return "rgb(200," + i * 8 + ",150)";
      },
      easing: "linear",
      duration: 2000,
    },
    strokeWidth: {
      value: 6,
      easing: "linear",
      delay: function (el, i) {
        return 1200 + i * 40;
      },
      duration: 800,
    },
    delay: function (el, i) {
      return i * 60;
    },
    duration: 1200,
    easing: "easeOutExpo",
    loop: true,
    direction: "alternate",
  });

  $anime({
    targets: "#hellosvg path",
    strokeDashoffset: [$anime.setDashoffset, 0],
    easing: "easeOutExpo",
    duration: 5000,
    delay: function (el, i) {
      return i * 500;
    },
    direction: "alternate",
    loop: false,
  });

  $anime({
    targets: "#arrow path",
    strokeDashoffset: [$anime.setDashoffset, 0],
    easing: "easeOutExpo",
    duration: 5000,
    delay: function (el, i) {
      return i * 500;
    },
    direction: "alternate",
    loop: false,
  });
});
